import './RedButton.css'
import profile from './images/profile.png'

const delay = ms => new Promise(res => setTimeout(res, ms));

const showResponseMessage = async () => {

  const element = document.getElementById("response-message");	// Get element
  const loading = document.getElementById("loader-container");

  loading.style.opacity = "0.5";
  await delay(2000);
  element.style.opacity = "1";	
  await delay(1000);
  element.style.opacity = "0";
  loading.style.opacity = "0";
  showResume();
};

export const showResume = () => {
  window.open('pdfs/CV - Erez Hasson.pdf', '_blank');
} 

function RedButton () {
  return (
    <div class="center">
      <div id="header" className="header">Resume Push Button 📃</div>

    <div className="phone-container">
      
      <div class="profile-card">

        <header>

          <a href="https://portfolio-erezhasson.web.app/">
            <img src={profile} />
          </a>
          <h1>Erez Hasson</h1>
          <h2>Software Programmer & Web Developer</h2>
        </header>

      <ul class="profile-social-links">

        <li>
            <a href="https://www.facebook.com/v1ctory">
              <svg viewBox="0 0 24 24">
                <path fill="#3b5998" d="M17,2V2H17V6H15C14.31,6 14,6.81 14,7.5V10H14L17,10V14H14V22H10V14H7V10H10V6A4,4 0 0,1 14,2H17Z" />
              </svg>
            </a>
        </li>

        <li>
          <a href="http://themeforest.net/user/Victory-Design">
            <svg viewBox="0 0 24 24">
              <path fill="#82B541" d="M18.334,1.909c-0.604-0.327-2.313-0.125-4.375,0.503c-3.621,2.464-6.664,6.11-6.89,11.971c-0.05,0.148-0.402-0.025-0.478-0.053c-0.981-1.859-1.358-3.846-0.554-6.688C6.189,7.39,5.686,7.089,5.611,7.165C5.435,7.34,4.681,8.145,4.178,9c-2.464,4.249-0.855,9.733,3.445,12.122c4.299,2.389,9.733,0.855,12.12-3.445C22.533,12.695,19.969,2.814,18.334,1.909z" />
            </svg>
          </a>
        </li>
        <li>
          <a href="https://codepen.io/VictoryDesign/">
          <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/></svg>
          </a>
        </li>
      </ul>

      <div class="select-button" onClick={showResponseMessage}></div>
      </div>

      <div id="loader-container">
          <div id="loader-dummy">
            <svg class="loader" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 19.8 5.3">
              <g id="loading-bullets">
                <circle class="loading-circle-1" cx="3.3" cy="2.7" r="1"/>
                <circle class="loading-circle-2" cx="9.8" cy="2.7" r="1"/>
                <circle class="loading-circle-3" cx="16.5" cy="2.7" r="1"/>
              </g>
            </svg>
          </div>
       </div>
    </div>
    <div id="response-message">Thank you for pressing the button :)</div>
    </div>
  );
}

export default RedButton;
